import { makeGetSet } from 'moment/src/lib/moment/get-set';
import { addFormatToken } from 'moment/src/lib/format/format';
import {
    addRegexToken,
    match1to2,
    match2,
    match1to2HasZero,
} from 'moment/src/lib/parse/regex';
import { addParseToken } from 'moment/src/lib/parse/token';
import { MINUTE } from 'moment/src/lib/units/constants';

// FORMATTING

addFormatToken('m', ['mm', 2], 0, 'minute');

// PARSING

addRegexToken('m', match1to2, match1to2HasZero);
addRegexToken('mm', match1to2, match2);
addParseToken(['m', 'mm'], MINUTE);

// MOMENTS

export var getSetMinute = makeGetSet('Minutes', false);
