import { addFormatToken } from 'moment/src/lib/format/format';
import { addRegexToken, match1 } from 'moment/src/lib/parse/regex';
import { addParseToken } from 'moment/src/lib/parse/token';
import { MONTH } from 'moment/src/lib/units/constants';
import toInt from 'moment/src/lib/utils/to-int';

// FORMATTING

addFormatToken('Q', 0, 'Qo', 'quarter');

// PARSING

addRegexToken('Q', match1);
addParseToken('Q', function (input, array) {
    array[MONTH] = (toInt(input) - 1) * 3;
});

// MOMENTS

export function getSetQuarter(input) {
    return input == null
        ? Math.ceil((this.month() + 1) / 3)
        : this.month((input - 1) * 3 + (this.month() % 3));
}
