import { makeGetSet } from 'moment/src/lib/moment/get-set';
import { addFormatToken } from 'moment/src/lib/format/format';
import {
    addRegexToken,
    match1to2,
    match2,
    match1to2HasZero,
} from 'moment/src/lib/parse/regex';
import { addParseToken } from 'moment/src/lib/parse/token';
import { SECOND } from 'moment/src/lib/units/constants';

// FORMATTING

addFormatToken('s', ['ss', 2], 0, 'second');

// PARSING

addRegexToken('s', match1to2, match1to2HasZero);
addRegexToken('ss', match1to2, match2);
addParseToken(['s', 'ss'], SECOND);

// MOMENTS

export var getSetSecond = makeGetSet('Seconds', false);
